import React from 'react'
import {Row, Col, Card, Form, Button} from 'react-bootstrap';
import Aux from "../../../hoc/_Aux";
import Swal from 'sweetalert2'
import {Redirect} from 'react-router-dom'
import { create, readsDistrict } from './api';


export default function Create() {

    const [values, setValues] = React.useState({
        teacherCode:"",
        school:"",
        firstName:"",
        middleName:"",
        lastName:"",
        title:"",
        gender:"",
        dob:"",
        nationality:"",
        qualification:"",
        email:"",
        phone:"",
        subjectTaught:"",
        classTaking:"",
        subjectSpecialisation:"",
        level:"",
        typeOfstaff:"",
        firstappt:"",
        lastPostiing:"",
        contractYears:"",
        retirementyear:"",
        gradeLevel:"",
        designation:"",
        serviceStatus:"",
        teachingTypes:"",
        teachingPeriodWK:"",
        Engagement:"",
        session:"",
        lastWorkshop:"",
        password:"",
        password2:"",
        loading:false,
        redirectToPage:false,
        districtList:[]
    })
    const {teacherCode, school,firstName, middleName,lastName,title,gender,dob,nationality,qualification,email, phone,subjectTaught, classTaking,subjectSpecialisation,level,typeOfstaff, firstappt, lastPostiing, contractYears, retirementyear, designation,serviceStatus,teachingTypes,teachingPeriodWK,Engagement,session, lastWorkshop, password, password2, loading, redirectToPage, districtList} = values

    const handleChange = name=>event=>{
        setValues({...values, [name]:event.target.value})
    }

    const submit = event =>{
        event.preventDefault();
        setValues({...values, loading:true})
        if(teacherCode===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Teacher code is required', 'error');
        }
        if(school===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'School Name is required', 'error');
        }
        if(firstName===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'First Name is required', 'error');
        }
        if(lastName===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Last Name is required', 'error');
        }
        if(title===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'title is required', 'error');
        }
        if(gender===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Gender is required', 'error');
        }
        if(dob===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Date of birth is required', 'error');
        }
        if(nationality===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'National is required', 'error');
        }
        if(qualification===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Qualification is required', 'error');
        }
        if(email===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Email is required', 'error');
        }
        if(phone===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Phone number is required', 'error');
        }
        if(subjectTaught===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Subject taught is required', 'error');
        }
        if(classTaking===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Classes taken is required', 'error');
        }
        if(subjectSpecialisation===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Subject Specialisation is required', 'error');
        }
        if(level===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Role is required', 'error');
        }
        if(typeOfstaff===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Staff type is required', 'error');
        }
        if(firstappt===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Date of First appointment is required', 'error');
        }
        if(gradeLevel===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Grade Level is required', 'error');
        }
        if(designation===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Designation is required', 'error');
        }
        if(serviceStatus===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Service Status is required', 'error');
        }
        if(teachingTypes===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Teaching Type is required', 'error');
        }
        if(session===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Session is required', 'error');
        }
        if(password==="") {
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Password must empty', 'error');
        }

        if(password !== password2) {
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Password must match each other', 'error');
        }
        handleCreate();
    }

    const handleCreate =async ()=>{
        //const user = {code, names,district, phone, email, password, password2, address,fax,mailBox,province,eduLevel,ownership,estabYear,schoolCat,schoolType,headID, loading, redirectToPage, districtList}
        const teacher = {teacherCode, school,firstName, middleName,lastName,title,gender,dob,nationality,qualification,email, phone,subjectTaught, classTaking,subjectSpecialisation,level,typeOfstaff, firstappt, lastPostiing, contractYears, retirementyear, designation,serviceStatus,teachingTypes,teachingPeriodWK,Engagement,session, lastWorkshop, password}
        const data = await create(teacher);
        console.log(data)
        if(!data){
            Swal.fire('Oops...', 'internet server error, Please, check your network connection', 'error')
            return setValues({...values, loading:false})
        }

        if(data.error){
            Swal.fire('Oops...', data.error, 'error')
            return setValues({...values, loading:false})
        }

        if(data.message){
            Swal.fire('Saved...', data.message, 'success')
           setValues({...values, loading:false, redirectToPage:true})
           let Toast = Swal.mixin({
            toast: true,
            timerProgressBar: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 3000
            });

            return Toast.fire({
                animation: true,
                type: 'success',
                title: 'Request is successful'
            })
        }
    }

    const redirectUser = () => {
        if (redirectToPage){
            return <Redirect to="/admin/schools/create" />
        }
    };

    React.useEffect(() => {
        const bootstrap = async () =>{
            const dist = await readsDistrict();
            let teacherCode = `SCH${`0012`}`;
            setValues(v => ({...v, districtList:dist.data, teacherCode})); 
        }
        bootstrap()
    }, [])

    return (
        <Aux>
            {redirectUser()}
            <Row>
                    <Col>
                        <Card>
                            <Card.Header>
                                <Card.Title as="h3">Add New Teacher</Card.Title>
                            </Card.Header>
                            <Card.Body>
                                <Row>
                                    <Col md={6}>
                                        <Form>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Teacher Code</Form.Label>
                                                <Form.Control type="text" placeholder="Teacher Code" onChange={handleChange("teacherCode")} value={teacherCode} disabled />
                                            </Form.Group>
                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>Title</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("title")} value={title}>
                                                <option>Select</option>
                                                <option value="Mr.">Mr.</option>
                                                <option value="Mrs.">Mrs.</option>
                                                <option value="Ms.">Ms.</option>
                                                <option value="Miss.">Miss.</option>
                                                <option value="Dr.">Dr.</option>
                                            </Form.Control>
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>First Name</Form.Label>
                                                <Form.Control type="text" placeholder="First Name" onChange={handleChange("firstName")} value={firstName} />
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Middle Name</Form.Label>
                                                <Form.Control type="text" placeholder="Middle Name" onChange={handleChange("middleName")} value={middleName} />
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Last Name</Form.Label>
                                                <Form.Control type="text" placeholder="Last Name" onChange={handleChange("lastName")} value={lastName} />
                                            </Form.Group>
                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>School</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("school")} value={school}>
                                                <option>Select School</option>
                                               {
                                                   schoolList && schoolList.length > 0 
                                                   ?
                                                   schoolList.map((scho, id)=>{
                                                       return(
                                                        <option value={scho._id}>{school.names}</option>
                                                       ) 
                                                   }) : <option value="0">Fails to fetch school</option>
                                               }
                                            </Form.Control>
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Address </Form.Label>
                                                <Form.Control type="text" placeholder="location, province e.g Veisaru Road, Savusavu" onChange={handleChange("address")} value={address} />
                                            </Form.Group>
                                           
                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>Locality</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("province")} value={province}>
                                                <option>Select locality</option>
                                                <option value="Metropolian">City Metropolian</option>
                                                <option value="Surburban">City Surburban</option>
                                                <option value="Urban">Peri Urban</option>
                                                <option value="Remote">Remote</option>
                                                <option value="Rural">Rural</option>
                                                <option value="Town">Town</option>
                                                <option value="Very-Remote">Very Remote</option>
                                            </Form.Control>
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Mailing Address </Form.Label>
                                                <Form.Control type="text" placeholder="P.O BOX 123, Tavua" onChange={handleChange("mailBox")} value={mailBox} />
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Phone </Form.Label>
                                                <Form.Control type="text" placeholder="official school phone number" onChange={handleChange("phone")} value={phone} />
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Fax</Form.Label>
                                                <Form.Control type="text" placeholder="school fax" onChange={handleChange("fax")} value={fax} />
                                            </Form.Group>
                                            <Form.Group controlId="formBasicEmail">
                                                <Form.Label>School Head</Form.Label>
                                                <Form.Control type="text" placeholder="staff ID" onChange={handleChange("headID")} value={headID} />
                                            </Form.Group>
                                        </Form>
                                    </Col>
                                    <Col md={6}>
                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>Education Level</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("eduLevel")} value={eduLevel}>
                                                <option>Select Education Level</option>
                                                <option value="ECCE">ECCE</option>
                                                <option value="Primary">Primary</option>
                                                <option value="Secondary">Secondary</option>
                                                <option value="TVET">TVET</option>
                                            </Form.Control>
                                            </Form.Group>
                                    <Form.Group controlId="formBasicEmail">
                                                <Form.Label>Estab. Year </Form.Label>
                                                <Form.Control type="date" placeholder="year founded" onChange={handleChange("estabYear")} value={estabYear} />
                                            </Form.Group>
                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>Ownership</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("ownership")} value={ownership}>
                                                <option>Select School Ownership</option>
                                                <option value="Community">Community</option>
                                                <option value="Faith-Based">Faith-Based</option>
                                                <option value="Government">Government</option>
                                                <option value="Private">Private</option>
                                                
                                            </Form.Control>
                                    </Form.Group>
                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>School Category</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("schoolCat")} value={schoolCat}>
                                                <option>Select School Category</option>
                                                <option value="Regular">Regular</option>
                                                <option value="Special">Special</option>
                                            </Form.Control>
                                     </Form.Group>
                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                            <Form.Label>School Type</Form.Label>
                                            <Form.Control as="select" onChange={handleChange("schoolType")} value={schoolType}>
                                                <option>Select School Type</option>
                                                <option value="Boarding">Boarding</option>
                                                <option value="Day">Day</option>
                                            </Form.Control>
                                     </Form.Group>
                                        <Form.Group controlId="exampleForm.ControlInput1">
                                            <Form.Label>email </Form.Label>
                                            <Form.Control type="email" placeholder="email" onChange={handleChange("email")} value={email}/>
                                        </Form.Group>
                                        <Form.Group controlId="formBasicPassword">
                                                <Form.Label>Password</Form.Label>
                                                <Form.Control type="password" placeholder="Password" onChange={handleChange("password")} value={password} />
                                            </Form.Group>
                                        <Form.Group controlId="formBasicPassword">
                                            <Form.Label>Password Confirmation</Form.Label>
                                            <Form.Control type="password" placeholder="Password Confirmation" onChange={handleChange("password2")} value={password2} />
                                        </Form.Group>
                                        
                                        {
                                                loading ? "loading ..." : <Button variant="primary" onClick={submit}  >Create ..</Button>
                                            }
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
        </Aux>
    )
}
