import React from 'react'
import {Row, Col, Card, Form, Button} from 'react-bootstrap';
import Aux from "../../../hoc/_Aux";
import Swal from 'sweetalert2'
import {Redirect, Link, useParams} from 'react-router-dom'
import { read, edit, readsSchool } from './api';
import {isAuthenticated} from '../../Auth/admin/api'

export default function Edit() {
    let { id } = useParams();
    const [values, setValues] = React.useState({
        studentCode:"",
        school:"",
        parent:"",
        stream:"",
        firstName:"",
        middleName:"",
        lastName:"",
        religion:"",
        gender:"",
        dob:"",
        country:"",
        ethnicity:"",
        disability:"",
        yearAdmission:"",
        presentClass:"",
        HadEce:"",
        subject:"",
        status:"",
        session:"",
        province:"",
        loading:false,
        redirectToPage:false,
        schoolList:[],
        parentList:[],
        subjectList:[],
        sessionList:[],
        classList:[]
    })
    const {studentCode, school, parent, presentClass,stream, firstName, middleName, lastName, gender, religion, dob,country, disability, yearAdmission, HadEce,subject, status, session, province,ethnicity, loading, redirectToPage, schoolList,parentList,subjectList,sessionList,classList} = values

    const handleChange = name=>event=>{
        setValues({...values, [name]:event.target.value})
    }

    const submit = event =>{
        event.preventDefault();
        setValues({...values, loading:true})
       {/*  if(studentCode===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Student code is required', 'error');
        }
        */}
        if(school===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'School is required', 'error');
        }
       {/*  if(parent===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'ParentID is required', 'error');
        }
        */}
        if(presentClass===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Present Class is required', 'error');
        }
        {/* if(stream===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Class Stream is required', 'error');
        }
        */}
        if(firstName===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'First Name  is required', 'error');
        }
        if(lastName===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Last Name is required', 'error');
        }
        if(gender===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Gender is required', 'error');
        }
        if(dob===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Date of Birth is required', 'error');
        }
        if(country===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Nationality is required', 'error');
        }
        if(disability===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Disability is required', 'error');
        }
        if(status===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Studentship status is required', 'error');
        }
        if(session===""){ 
            setValues({...values, loading:false})
            return Swal.fire('Oops...', 'Academic session is required', 'error');
        }
        handleUpdate();
    }

    const handleUpdate =async ()=>{
        const Auth = await isAuthenticated();
        const student = {school, parent, presentClass, stream, firstName, middleName, lastName, gender, religion, dob, country, disability, yearAdmission, HadEce, subject, province}
        const data = await edit(id,student, Auth.token);
        if(!data){
            Swal.fire('Oops...', 'internet server error, Please, check your network connection', 'error')
            return setValues({...values, loading:false})
        }
        if(data.error){
            Swal.fire('Oops...', data.error, 'error')
            return setValues({...values, loading:false})
        }
        if(data.message){
            Swal.fire('Saved...', data.message, 'success')
           setValues({...values, loading:false, redirectToPage:true})
           let Toast = Swal.mixin({
            toast: true,
            timerProgressBar: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 3000
            });

            return Toast.fire({
                animation: true,
                type: 'success',
                title: 'Request is successful'
            })
        }
    }

    const redirectUser = () => {
        if (redirectToPage){
            return <Redirect to="/admin/students/read" />
        }
    };

    React.useEffect(() => {
        const bootstrap = async () =>{
            const Auth = await isAuthenticated()
            const scho = await readsSchool(Auth.token);
            const stud = await read(id,Auth.token);
            //console.log({stud})
            const {school, parent, presentClass, stream, firstName, middleName, lastName, gender, religion, dob, country, disability, yearAdmission, HadEce, subject, province } = stud
            setValues(v => ({...v, schoolList:scho.data, classList:cla.data, studentCode, parent, presentClass, stream, firstName, middleName, lastName, gender, religion, dob, country, disability, yearAdmission, HadEce, subject, province})); 
        }
        bootstrap()
    }, [id])

    return (
        <Aux>
        {redirectUser()}
        <Row>
                    <Col>
                        <Card.Header>
                            <Card.Title><Link to="/admin/students/read" > Read Students </Link></Card.Title>
                        </Card.Header>
                    </Col>
        </Row>
        <Row>
                <Col>
                    <Card>
                        <Card.Header>
                            <Card.Title as="h3">Update Student</Card.Title>
                        </Card.Header>
                        <Card.Body>
                            <Row>
                                <Col md={6}>
                                    <Form>
                        {/*             <Form.Group controlId="formBasicEmail">
                                            <Form.Label>Student Code</Form.Label>
                                            <Form.Control type="text" placeholder="school code" onChange={handleChange("studentCode")} value={studentCode} />
                                        </Form.Group>
                         */}
