import React from 'react';
import {Row, Col, Card, Button,} from 'react-bootstrap';
import Aux from "../../../hoc/_Aux";
import Swal from 'sweetalert2'
import { reads} from './api';
import { Link } from 'react-router-dom';
import moment from 'moment'; 
import Datatable from 'react-bs-datatable'; 
import {isAuthenticated} from '../../Auth/district/api'


export default function Read() {
    const [datas, setDatas] = React.useState([]);
    const [loading, setLoading] = React.useState(false)
    const [error, setError] = React.useState(false)
    const [reload, setReload] = React.useState(false)

 
    const isLoading = () => {
        if (loading){
            return (
                <Aux>
                    <Row>
                        <Col>
                        <Card>
                            <Card.Header>
                                <Card.Title as="h3">Wait loading data ...</Card.Title>
                            </Card.Header>
                            <Card.Body>
                                <Row>
                                    <Col>
                                    <h1>School List is loading </h1>
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                        </Col>
                    </Row>
                </Aux>
            )
        }
    };

    const isError = () => {
        if(error){
            return (
                <Aux>
                    <Row>
                        <Col>
                        <Card>
                            <Card.Header>
                                <Card.Title as="h3">Error in Loading Data.</Card.Title>
                            </Card.Header>
                            <Card.Body>
                                <Row>
                                    <Col>
                                        <h1>School data failed to Load, you can refresh <Button variant="primary" onClick={handleReload}>Reload</Button> </h1>
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                        </Col>
                    </Row>
                </Aux>
            )
        }
    };

    const handleReload = event =>{
        event.preventDefault();
        setError(false)
        setLoading(true)
        setReload(!reload) 
    }
    const boot = async () => {
        const Auth = await isAuthenticated()
        // const district = Auth.district._id
        // console.log({Auth})
        setLoading(true)
        const data = await reads(Auth.district._id, Auth.token);
        if(!data){
            Swal.fire('Oops...', 'internet server error, Please, check your network connection', 'error')
            setLoading(false)
            setError(true)
            return 
        }
        if(data.error){
            Swal.fire('Oops...', data.error, 'error')
            setLoading(false)
            setError(true)
            return
        }
        if(data.message){
            setLoading(false)
            setError(false)
            setDatas(data.data)
            Swal.fire('Successful....', data.message, 'success');
            let Toast = Swal.mixin({
                toast: true,
                timerProgressBar: true,
                position: 'top-end',
                showConfirmButton: false,
                timer: 3000
            });
    
            return Toast.fire({
                showClass: true,
                type: 'success',
                title: data.message
            })
        }
    }

    React.useEffect(() => {
        boot()
    },[reload])

    const ViewData = () =>{
        return(
          <Aux>
            <h1>Manage School data</h1>
            <hr />
            {
              datas.length > 0 ?  
              <Datatable 
                tableHeaders={header} 
                tableBody={body(datas)} 
                rowsPerPage={10}
                rowsPerPageOption={[5, 10, 15, 20, 30, 40, 50, 100]}
              />
              : <h1>No Data </h1>
            }
          </Aux>
        )
      }
      const header = [
        { title: 'SN', prop: 'id', filterable: true, sortable: true, },
        { title: 'Full Names', prop: 'names', filterable: true, sortable: true, },
        //{ title: 'District', prop: 'district', filterable: true, sortable: true, },
        { title: 'Edu Level', prop: 'eduLevel', filterable: true, sortable: true },
        { title: 'category', prop: 'category', filterable: true, sortable: true },
        { title: 'Type', prop: 'type', filterable: true, sortable: true },
        { title: 'Ownership', prop: 'owner', filterable: true, sortable: true },
        { title: 'Created On ', prop: 'date', filterable: true, sortable: true },
        { title: 'Last Update', prop: 'date', filterable: true, sortable: true, },
        { title: 'Details', prop: 'edit', cell: row =><Link to={`/district/schools/edit/${row.edit}`} > Edit</Link>},
        { title: 'Details', prop: 'delete', cell: row =><Link to={`/district/schools/delete/${row.delete}`} > Delete </Link>},
        { title: 'Details', prop: 'detail', cell: row =><Link to={`/district/schools/read/${row.detail}`} > Detail </Link>},
      ];
    
      const body = (dat) => {
        return dat.map((data, index)=>{
            // console.log(JSON.stringify({data}))
          return{
            id:index +1,
            names:data.names,
           /// district:data.fromDistrict[0].names,
            eduLevel:data.eduLevel,
            category:data.schoolCat,
            type:data.schoolType,
            owner:data.ownership,
            date :moment(data.createdAt,"YYYY-MM-DDTHH:mm:ss.SSSSZ").format('LL'),
            date :moment(data.updatedAt,"YYYY-MM-DDTHH:mm:ss.SSSSZ").format('LL'),
            edit:data._id,
            delete:data._id,
            detail:data._id,
          }
        })
      };

    return (
        <Aux>
          
            {error ? isError() : loading ?  isLoading() :ViewData()}
        </Aux>
    )
}
